sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var variableJson = inputs.requestbody != '' ? JSON.parse(inputs.requestbody).body : '';
  var gr= new GlideRecord(inputs.table);
  if(inputs.method == 'POST'){
  gr.initialize();
  gr.applyEncodedQuery(inputs.query);
   for (var key in variableJson) {
    gr.getElement(key).setDisplayValue(variableJson[key]);
           // gr[key]=variableJson[key];
   }
  gr.insert();
  }else if(inputs.method == 'PUT'){
    gr.get('number',variableJson['number']);
    for (var pkey in variableJson) {
      if(pkey != 'number')
        gr.getElement(pkey).setDisplayValue(variableJson[pkey]);
            //gr[pkey]=variableJson[pkey];
   }
    gr.update();
  }
  if(gr.number != '')
  outputs.sid=gr.number;
  else
  outputs.sid=gr.number;
})(inputs, outputs);

