sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var Rest= inputs.RestMessage;
  var method= inputs.RestMethod;
  var variableJson= inputs.VariableJSON != '' ? JSON.parse(inputs.VariableJSON) : '';
  var basicString= inputs.BasicAuthString;
  var basicAuthProf= inputs.BasicAuthProfile;
    var rest = new sn_ws.RESTMessageV2(Rest, method);
    if(basicAuthProf.username != '' && basicAuthProf.username != 'undefined' && basicAuthProf.username != undefined){
    var ge = new GlideEncrypter();
    rest.setStringParameter('password', ge.decrypt(basicAuthProf.password));
	rest.setStringParameter('username', basicAuthProf.username);
    }
    if(inputs.Password != ''){
     var gp = new GlideEncrypter();
     rest.setStringParameter('password', gp.decrypt(inputs.Password));
    }  
    if(variableJson != ''){
      for(var key in variableJson){
        rest.setStringParameter(key, variableJson[key]);
      }
    }
  	//var response = rest.execute();
	var response = rest.executeAsync();
	response.waitForResponse(60);
    var responseBody = response.getBody();
    var httpcode=response.getStatusCode();
    outputs.error_message=response.haveError() ? response.getErrorMessage() : "";
	outputs.http_status = httpcode;
	outputs.response_body = responseBody ;
	outputs.isSuccess= httpcode == 200 || httpcode == '200'?'true':'false';
})(inputs, outputs);

