sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try {
		var input = {};
		input['inbound_request'] = inputs.InboundRequest;
		input['flow_config'] = inputs.flow_con;
		input['request_body'] = inputs.RequestBody;
    var sf=inputs.app_name+'.'+inputs.flow_name;
    gs.info('Action '+sf);
		var result = sn_fd.FlowAPI.getRunner().subflow(sf).inForeground().withInputs(input).run();
		var output = result.getOutputs();
		var output = output['output']; // String
    gs.info('Action 2'+output);
	} catch (ex) {
		var message = ex.getMessage();
		gs.error(message);
	}
  outputs.status=output;
})(inputs, outputs);

